<div style="  height: 400px; overflow-y: scroll; padding-right: 8px;"> <div style="display: none; padding: 10px;" class="sh-ad"> <label><input type="radio" name="search-ad" class="search-all" checked >  ຄົ້ນຫາທັງໝົດ</label> <br> <label><input type="radio" name="search-ad" class="search-kine" >  ຄົ້ນຫາຕາມໝວດ</label> <div class="sh-search-kine" style="display: none;"> <div class="well well-sm" > <div class="row"> <div class="col-md-6"> <label><input type="radio"  class="doctin" name="doct" > ເອກະສານຂາເຂົ້າ</label> <select name="" class="form-control select-in"> <?php foreach($sh_gno_in  as $grono){  ?> <option value="<?php echo $grono['id']; ?>"> <?php echo $grono['grono_name']; ?></option> <?php } ?> </select> </div> <div class="col-md-6"><label> <input type="radio"  class="doctout" name="doct" > ເອກະສານຂາອອກ</label> <select name="" class="form-control select-out"> <?php foreach($sh_gno_out  as $grono){  ?> <option value="<?php echo $grono['id']; ?>"> <?php echo $grono['grono_name']; ?></option> <?php } ?> </select> </div> </div> </div> </div> </div> <span class="load_search"></span> <ul class="list-group show-search" > <?php if($show_in_out == null){ echo '<div class="alert alert-warning alert-dismissible" role="alert"><button type="button" class="close" data-dismiss="alert" aria-label="Close"><span aria-hidden="true">&times;</span></button> <strong> ບໍ່ພົບຂໍ້ມູນ ທີ່ທ່ານຄົ້ນຫາ!</strong> <span class="txt_no"></span> </div>'; } ?> </ul> </div> <div class="modal fade " id="myModal" tabindex="-1" role="dialog" aria-labelledby="myModalLabel"> <div class="modal-dialog" role="document"> <div class="modal-content"> <div class="modal-header"> <button type="button" class="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span></button> <h4 class="modal-title" id="myModalLabel"><i class="fa fa-file-pdf-o"></i> ໄຟລ໌ເອກະສານ</h4> </div> <div class="modal-body sh_file"></div> <div class="modal-footer"> <button type="button" class="btn btn-default btn_print" data="" > <span class="glyphicon glyphicon-print" aria-hidden="true"></span> ສັ່ງພິມ</button> </div> </div> </div> </div> <input type="hidden" class="data_year" value="<?php echo $used_year->name; ?>" > <input type="hidden" value="" id="clicklist"> <script type="text/javascript"> function showdetail(id) {var cl = $('#clicklist').val(); if(cl == id){$('.doc_detail').slideUp('slow'); $('#clicklist').val(''); } else {$('.doc_detail').slideUp('slow'); $('#doc'+id).slideDown('slow'); $('#clicklist').val(id);} } var urlh = window.location.href; var url = urlh.split('#')[0]; function shfile(file) {var sfile = file.split("."); if(sfile[1]=='pdf'){if( /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent) ) {$('.sh_file').html('<form method="get" action="'+url+'file_upload/'+file+'"><center><button type="submit" class="btn btn-default" ><i class="fa fa-file-pdf-o"></i> ດາວໂຫລດເອກະສານ PDF</button></center></form>'); } else {$('.sh_file').html('<object data="'+url+'file_upload/'+file+'" type="application/pdf" width="100%" height="500"></object>'); } } else {$('.sh_file').html('<image src="'+url+'file_upload/'+file+'" width="100%" >'); } $('.btn_print').attr('data', file); $('#myModal').modal('show'); } $('.btn_print').click(function(event) {var file = $('.btn_print').attr('data'); var w = window.open(url+'file_upload/'+file); $(w).ready(function(){w.print(); }); }); $('.search-ada').click(function(event) {$('.sh-ad').slideDown('slow', function() {$('.search-kine').click(function(event) {$('.sh-search-kine').slideDown('slow'); $('.select-in').attr('disabled', true); $('.select-out').attr('disabled', true); search(); }); $('.doctin').click(function(event) {$('.select-in').attr('disabled', false); $('.select-out').attr('disabled', true); search(); }); $('.doctout').click(function(event) {$('.select-out').attr('disabled', false); $('.select-in').attr('disabled', true); search(); }); $('.select-out').change(function(event) { search()  }); $('.select-in').change(function(event) { search() }); $('.search-all').click(function(event) {$('.sh-search-kine').slideUp('slow'); $('.doctin').attr('checked', false); $('.doctout').attr('checked', false); search(); }); }); }); $('#search_doc').keyup(function(event) { search() }); search(); function search() {var data_year = $('.data_year').val(); $('.load_search').html('<div class="text-primary text-center"><i class="fa fa-refresh fa-spin" ></i> ລະບົບກຳລັງຄົ້ນຫາ...</div>'); var urlh = window.location.href; var urli = urlh.split('#')[0]; var data_search = $('#search_doc').val(); if($('.doctin').is(":checked")){var dat = $('.select-in').val(); $.post(urli+'index.php/'+'load_page',{ page:'search-data',data:data_search,search_kine:'in',gtt:dat,year:data_year} ,function(data){ $('.show-search').html(data); $('.load_search').html(''); $('.txt_no').html('ຄຳຄົ້ນຫາ \"'+data_search+'\" ບໍ່ມີໃນຖານຂໍ້ມູນ.'); }); } else if($('.doctout').is(":checked")){var dat = $('.select-out').val(); $.post(urli+'index.php/'+'load_page',{ page:'search-data',data:data_search,search_kine:'out',gtt:dat,year:data_year} ,function(data){ $('.show-search').html(data); $('.load_search').html(''); $('.txt_no').html('ຄຳຄົ້ນຫາ \"'+data_search+'\" ບໍ່ມີໃນຖານຂໍ້ມູນ.'); }); } else {$.post(urli+'index.php/'+'load_page',{ page:'search-data',data:data_search,search_kine:'all',year:data_year} ,function(data){ $('.show-search').html(data); $('.load_search').html(''); $('.txt_no').html('ຄຳຄົ້ນຫາ \"'+data_search+'\" ບໍ່ມີໃນຖານຂໍ້ມູນ.'); }); } } </script>